mapper.insert:
insert into sc_invoice_management ( order_id, invoice_money, 
  create_user, create_time, invoice_type, invoice_baseid, `status`,  mid, pmid
  )values ( #{orderId,jdbcType=VARCHAR}, #{invoiceMoney,jdbcType=DOUBLE}, 
  #{createUser,jdbcType=INTEGER}, now(), #{invoiceType,jdbcType=INTEGER}, #{invoiceBaseid,jdbcType=INTEGER}, #{status,jdbcType=INTEGER}, 
 #{mid,jdbcType=INTEGER}, #{pmid,jdbcType=INTEGER}
  )

mapper.select:
select id, order_id, invoice_money, drawer, create_user, create_time, update_time, invoice_type,
invoice_baseid, `status`, invoice_url,invoice_num, mid, pmid
from sc_invoice_management


     and status=#{status}


     and pmid=#{pmid}


     and mid in (${mid})


     and order_id like '%${orderId}%' 


    AND create_time >= #{stime}


    AND create_time <= #{etime}


    AND invoice_num = #{invoiceNum}



order by id desc

mapper.update:
update sc_invoice_management
set 
  drawer = #{drawer,jdbcType=INTEGER},
  update_time = now(),
  `status` = #{status,jdbcType=INTEGER}

    ,invoice_url = #{invoiceUrl,jdbcType=VARCHAR}


     ,invoice_num = #{invoiceNum,jdbcType=VARCHAR}

where id = #{id,jdbcType=INTEGER}
