mapper.insert:
insert into sc_maintain_price (id, `date`, pmid, 
  kind, variety_name, material, 
  specification, factory, storehouse, 
  price, `status`, create_time
  )
values (#{id,jdbcType=INTEGER}, #{date,jdbcType=DATE}, #{pmid,jdbcType=INTEGER}, 
  #{kind,jdbcType=TINYINT}, #{varietyName,jdbcType=VARCHAR}, #{material,jdbcType=VARCHAR}, 
  #{specification,jdbcType=VARCHAR}, #{factory,jdbcType=VARCHAR}, #{storehouse,jdbcType=VARCHAR}, 
  #{price,jdbcType=DOUBLE}, #{status,jdbcType=TINYINT}, #{createTime,jdbcType=TIMESTAMP}
  )

mapper.update:
update sc_maintain_price
set `status`=0
where pmid=#{pmid} and date=#{date} and kind =#{kind}
