mapper.select:
select id, `type`, price, review_price, pmid, `status`, review_status, `date`
from sc_today_basePrice_info
where date = #{date,jdbcType=DATE} and status = 1 and pmid =#{pmid}

mapper.update:
update sc_today_basePrice_info
set status = 0

  date = #{date,jdbcType=DATE} and pmid =#{pmid}
  
    and review_status =#{reviewStatus}
