mapper.update:
update Province2Area
set ProviceId = #{proviceid,jdbcType=INTEGER},
  ProviceName = #{provicename,jdbcType=VARCHAR},
  Area = #{area,jdbcType=VARCHAR}
where ID = #{id,jdbcType=INTEGER}

mapper.select:
select ID, ProviceId, ProviceName, Area
from Province2Area
where ID = #{id,jdbcType=INTEGER}

select ID, ProviceId, ProviceName, Area
from Province2Area
where ProviceId = #{id,jdbcType=INTEGER}
